sql.where:

				AND S.BIZ_REG_NO = #{bizRegNo}





	                    AND S.ID LIKE CONCAT ('%', #{searchWrd},'%') 		


	                    AND S.NAME LIKE CONCAT ('%', #{searchWrd},'%') 		



            AND NOT EXISTS (
			    SELECT 1
			    FROM ATOS_ENROLLMENT E
