mapper.update:
UPDATE ATOS_LECTURE
SET
	INSTRUCTOR = #{id}
WHERE LECTURE_CODE = #{lectureCode}

UPDATE ATOS_INSTRUCTOR_ASSIGNMENTS
SET
	INSTRUCTOR = #{id}
WHERE LECTURE_CODE = #{lectureCode}

mapper.delete:
DELETE FROM ATOS_INSTRUCTOR_ASSIGNMENTS
WHERE LECTURE_CODE = #{lectureCode}
AND ASSIGNMENTS_DATE != (
	SELECT LEARN_DATE	
	FROM ATOS_LECTURE
	WHERE LECTURE_CODE = #{lectureCode}	
)
